DROP TABLE IF EXISTS "STAFF";
CREATE TABLE STAFF (
        id INTEGER PRIMARY KEY,
        firstName CHAR(40)  NOT NULL ,
        patronym CHAR(40),
        lastName CHAR (40)  NOT NULL ,
        birthDate CHAR (10) NOT NULL ,
        jobPosition CHAR (40),
        department CHAR (40),
        room CHAR(10),
        tableId CHAR (10),
        uid CHAR (20),
        UNIQUE (tableId),
        FOREIGN KEY (uid) REFERENCES RFIDTAGS(uid));
